import React, { useState } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { role: 'bot', text: "Hey! I’m Echo. Ask me what you said on any day 📅" }
  ]);
  const [input, setInput] = useState('');

  // 🧠 Dummy memory log
  const memory = [
    {
      date: "2025-07-01",
      summary: "Talked about Tensorik community ideas.",
      tags: ["Tensorik", "community", "project"]
    },
    {
      date: "2025-06-30",
      summary: "Planned AI hackathon project 'Echo'.",
      tags: ["hackathon", "Echo", "planning"]
    },
    {
      date: "2025-06-29",
      summary: "Discussed exam stress and time management.",
      tags: ["college", "stress", "exams"]
    }
  ];

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);

    // ✨ Simulate thinking
    setTimeout(() => {
      const query = input.toLowerCase();
      const matched = memory.find(
        (m) =>
          m.summary.toLowerCase().includes(query) ||
          m.tags.some((t) => query.includes(t.toLowerCase())) ||
          query.includes(m.date)
      );

      const botMessage = matched
        ? {
            role: 'bot',
            text: `You said on ${matched.date}: "${matched.summary}"`
          }
        : {
            role: 'bot',
            text: "Hmm, I couldn't find anything related to that. 🧐"
          };

      setMessages((prev) => [...prev, botMessage]);
    }, 800); // Fake delay

    setInput('');
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Echo 🧠</div>

      <div className="chat-messages">
        {messages.map((msg, i) => (
          <div key={i} className={`chat-bubble ${msg.role}`}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="chat-input-area">
        <input
          type="text"
          placeholder="Ask Echo anything..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

export default App;
