import React, { useState } from 'react';
import VoiceRecorder from './VoiceRecorder';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { role: 'bot', text: "Hey! Iâ€™m Echo. Ask me what you said on any day ðŸ“…" }
  ]);
  const [input, setInput] = useState('');

  // ðŸ§  Dummy memory log
  const memory = [
    {
      date: "2025-07-01",
      summary: "Talked about Tensorik community ideas.",
      tags: ["Tensorik", "community", "project"]
    },
    {
      date: "2025-06-30",
      summary: "Planned AI hackathon project 'Echo'.",
      tags: ["hackathon", "Echo", "planning"]
    },
    {
      date: "2025-06-29",
      summary: "Discussed exam stress and time management.",
      tags: ["college", "stress", "exams"]
    }
  ];

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);

    // âœ¨ Simulate thinking
    setTimeout(() => {
      const query = input.toLowerCase();
      const matched = memory.find(
        (m) =>
          m.summary.toLowerCase().includes(query) ||
          m.tags.some((t) => query.includes(t.toLowerCase())) ||
          query.includes(m.date)
      );

      const botMessage = matched
        ? {
            role: 'bot',
            text: `You said on ${matched.date}: "${matched.summary}"`
          }
        : {
            role: 'bot',
            text: "Hmm, I couldn't find anything related to that. ðŸ§"
          };

      setMessages((prev) => [...prev, botMessage]);
    }, 800); // Fake delay

    setInput('');

    const handleTranscribe = (data) => {
  const { text, summary, tags } = data;

  const userMessage = { role: 'user', text };
  const botMessage = {
    role: 'bot',
    text: `Summary: "${summary}"\nTags: ${tags.join(', ')}`
  };

  setMessages((prev) => [...prev, userMessage, botMessage]);
};

  };



return (
  <div className="chat-container">
    <h1>Echo ðŸ§ </h1>
    <VoiceRecorder onTranscribe={handleTranscribe} />
    <div className="chat-box">
      {messages.map((msg, index) => (
        <div
          key={index}
          className={`message ${msg.type === 'user' ? 'user' : 'bot'}`}
        >
          {msg.text}
        </div>
      ))}
    </div>
    <div className="chat-input">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Ask Echo anything..."
      />
      <button onClick={handleSend}>Send</button>
    </div>
  </div>
);
}

export default App;
